import React, { useState, useEffect } from 'react';
import CuadroConTexto from '../components/CuadroConTexto';
import '../styles/skills.css';

const Skills = () => {
  const [isSmallScreen, setIsSmallScreen] = useState(false);

  useEffect(() => {
    const handleResize = () => {
      setIsSmallScreen(window.innerWidth <= 768);
    };

    handleResize(); // Verificar el tamaño inicial
    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  const textos = [
    'UI Design',
    'Accesibilidad UX',
    'Diseño Responsivo',
    'Design System',
    'Ilustración',
    'Prototipado',
  ];

  return (
    <div id="skills" className="skills">
      <div className="skills-titulo">
        Skills 
      </div>

      <div className="skills-cuadros-container">
        {isSmallScreen ? (
          <>
            <div className="skills-cuadros-group">
              <CuadroConTexto texto="UI Design" />
              <CuadroConTexto texto="Accesibilidad UX" />
            </div>
            <div className="skills-cuadros-group">
              <CuadroConTexto texto="Diseño Responsivo" />
            </div>
            <div className="skills-cuadros-group">
              <CuadroConTexto texto="Design System" />
              <CuadroConTexto texto="Ilustración" />
              <CuadroConTexto texto="Prototipado" />
            </div>
          </>
        ) : (
          /* Estructura para dispositivos no pequeños */
          <div className="skills-cuadros-group">
            {textos.map((texto, index) => (
              <CuadroConTexto key={index} texto={texto} />
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Skills;
