import React, { useState, useEffect } from 'react';
import '../styles/NavBar.css';

function NavBar() {
  const [prevScrollPos, setPrevScrollPos] = useState(0);
  const [visible, setVisible] = useState(true);

  const handleScroll = () => {
    const currentScrollPos = window.scrollY;
    const isVisible = prevScrollPos > currentScrollPos || currentScrollPos < 100;

    setPrevScrollPos(currentScrollPos);
    setVisible(isVisible);
  };
  const handleMenuClick = () => {
    setMenuOpen(!menuOpen);
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [prevScrollPos]);
  return (
    <nav className={`navbar ${visible ? 'visible' : 'hidden'}`}>
      <button onClick={handleMenuClick}>&#9776;</button>
      <ul style={{ display: menuOpen ? 'flex' : 'none' }}>
        <li><a href="#portada">Home</a></li>
        <li><a href="#quien">Quién Soy</a></li>
        <li><a href="#skills">Skills</a></li>
        <li><a href="#softwares">Softwares</a></li>
        <li><a href="#experiencia">Experiencia</a></li>
      </ul>
    </nav>
  );
  
  return (
    <nav className={`navbar ${visible ? 'visible' : 'hidden'}`}>
      <ul>
        <li><a href="#portada">Home</a></li>
        <li><a href="#quien">Quién Soy</a></li>
        <li><a href="#skills">Skills</a></li>
        <li><a href="#softwares">Softwares</a></li>
        <li><a href="#experiencia">Experiencia</a></li>
      </ul>
    </nav>
  );
}

export default NavBar;
