// Skills.js
import React from 'react';
import CuadroConTexto from '../components/CuadroConTexto';
import '../styles/skills.css';

const Skills = () => {
  const [isSmallScreen, setIsSmallScreen] = useState(false);

  useEffect(() => {
    const handleResize = () => {
      setIsSmallScreen(window.innerWidth <= 768);
    };

    handleResize(); // Verificar el tamaño inicial
    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  const textos = [
    'UI Design',
    'Accesibilidad UX',
    'Diseño Responsivo',
    'Design System',
    'Ilustración',
    'Prototipado',
  ];

    const group1 = textos.slice(0, 3);
  const group2 = textos.slice(3);

  return (
    <div id="skills" className="skills">
      <div  className="skills-titulo">
        Skills 
      </div>


    <div className="skills-cuadros-container">
      <div className="skills-cuadros-group">
        <CuadroConTexto texto="UI Design" />
        <CuadroConTexto texto="Accesibilidad UX" />
        <CuadroConTexto texto="Diseño Responsivo" />
      </div>
    
      <div className="skills-cuadros-group-1">
        <CuadroConTexto texto="Design System" />
        <CuadroConTexto texto="Ilustración" />
        <CuadroConTexto texto="Prototipado" />
      </div>
    </div>
    </div>
  );
  return (
    <div id="skills" className="skills">
      <div className="skills-titulo">Skills</div>
  
      <div className="skills-cuadros-container">
        {isSmallScreen ? (
          // Renderizar dos grupos para dispositivos pequeños
          <>
            <div className="skills-cuadros-group">
              {group1.map((texto, index) => (
                <CuadroConTexto key={index} texto={texto} />
              ))}
            </div>
            <div className="skills-cuadros-group">
              {group2.map((texto, index) => (
                <CuadroConTexto key={index} texto={texto} />
              ))}
            </div>
          </>
        ) : (
          // Renderizar un solo grupo para dispositivos grandes
          <div className="skills-cuadros-group">
            {textos.map((texto, index) => (
              <CuadroConTexto key={index} texto={texto} />
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Skills;
